import * as ABI from "../../abis"
import ContractBase from "../contract-base"
import { ConnectionConfig } from "../../types/eth"

export default class UniswapV2Factory extends ContractBase<typeof ABI.UniswapV2FactoryABI> {
  constructor(connectionConfig: ConnectionConfig, contractAddress: string) {
    super(connectionConfig, contractAddress, ABI.UniswapV2FactoryABI)
  }

  async getPair(token1Address: string, token2Address: string) {
    return this.contract.methods.getPair(token1Address, token2Address).call()
  }
}
