import UniswapV2ConnectorABI from "../../abis/teleswap/UniswapV2Connector"
import ContractBase from "../contract-base"
import { ConnectionConfig } from "../../types"

export class UniswapV2Connector extends ContractBase<typeof UniswapV2ConnectorABI> {
  constructor(connectionConfig: ConnectionConfig, contractAddress: string) {
    super(connectionConfig, contractAddress, UniswapV2ConnectorABI)
  }

  isPathValid(path: string[]) {
    return this.contract.methods.isPathValid(path).call()
  }
}
export default UniswapV2Connector
