import UniswapV3ConnectorABI from "../../abis/teleswap/UniswapV3Connector"
import ContractBase from "../contract-base"
import { ConnectionConfig } from "../../types"

export class UniswapV3Connector extends ContractBase<typeof UniswapV3ConnectorABI> {
  constructor(connectionConfig: ConnectionConfig, contractAddress: string) {
    super(connectionConfig, contractAddress, UniswapV3ConnectorABI)
  }

  async isPathValid(path: string[]) {
    return this.contract.methods.isPathValid(path).call()
  }

  async getExactInput(path: string[], amount: string | number) {
    return this.contract.methods.getExactInput(path, amount).call()
  }
  async getExactOutput(path: string[], amount: string | number) {
    return this.contract.methods.getExactOutput(path, amount).call()
  }
}
export default UniswapV3Connector
